import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

import WebGraph from "./WebGraph";
import createInfoBox from "./CreateInfoBox";

const GraphPage = () => {
  const { state } = useLocation();
  const [datasets, setDatasets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (state && state.graphData) {
      setDatasets(state.graphData);
      setLoading(false);
    } else {
      console.error("No graph data found in state.");
      setLoading(false);
    }
  }, [state]);

  return (
    <div>
      {loading ? (
        <p>Loading data...</p>
      ) : (
        datasets.length > 0 && (
          <WebGraph
            datasets={datasets}
            renderInfoBox={(node) =>
              createInfoBox(
                node.title,
                `${node.link}`,
                `This is node ${node.title}.`
              )
            }
            width={800}
            height={600}
          />
        )
      )}
      {datasets.length === 0 && !loading && <p>No data found for this graph.</p>}
    </div>
  );
};

export default GraphPage;
